import banidb
import pickle

def getShabadMatches(verseInit):
    matchingShabads = banidb.search(verseInit)
    matchStore = {}
    for page, matches in matchingShabads['pages_data'].items():
        for shabadInd, shabad in enumerate(matches):
            print(f"{shabadInd+1}. {shabad['verse']}")
            matchStore[shabadInd+1] = {'shabad_id': shabad['shabad_id'], 'verse': shabad['verse']}

    shabadChoice = eval(input("Enter shabad number: "))
    try:
        shabadId = matchStore[shabadChoice]['shabad_id']
        # print(shabadId)
        chosenShabad = banidb.shabad(shabadId)
        # print(chosenShabad)
        for ver in chosenShabad['verses']:
            print(ver['verse'])
    except Exception as e:
        print(e)

shabadInit = input("Find Shabad: ")
getShabadMatches(shabadInit)
